import React, { useState } from 'react';
import Axios from 'axios'
import { Series } from './model/series-tv';
import css from './App.module.css';

export const App = () => {

  const [text, setText] = useState<string>('soprano')
  const [result, setResult] = useState<Series[]>([]);

  const onChangeHandler  = (event: React.ChangeEvent<HTMLInputElement>) =>{
    setText(event.target.value);
  };

  const searchHandler = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    Axios.get(`http://api.tvmaze.com/search/shows?q=${text}`)
      .then(res => setResult(res.data));
  };

  const itemClickHandler = (series : Series) => {
    window.open(series.show.url);
  };

  return (
    <div>
      <form onSubmit={searchHandler}>
        <input
          type="text"
          placeholder="Search TV Series"
          value={text}
          onChange={onChangeHandler}
        />
      </form>

      {result.length} results

      <div className={css.grid}>
        {
          result.map((series) => {

            const {show: { id, image, name}} = series;

            return <div key={id} className={css.gridItem}>
              <div className={css.movie} onClick={itemClickHandler}>
                {
                  image ? <img src={image?.medium} alt="" /> : <NoImage></NoImage>
                }
                <div className={css.movieText}>{name}</div>
              </div>
            </div>
          })
        }
      </div>
    </div>
  );
}

export default App;

export const NoImage = () => {
  return <div className={css.noImage}></div>
}
